mapper.select:
select * from teacherClass


        and teacherNumber = #{teacherNumber}


        and classNumber = #{classNumber}


        and term = #{term}


        and teacherClassId = #{teacherClassId}

SELECT term, max(score) maxScore, min(score) minScore, AVG(score) avgScore, classId FROM teacherClass a JOIN studentClass b ON a.teacherClassId = b.teacherClassId
and teacherId = #{teacherId}
GROUP BY term, classId
