mapper.update:
update student_clazz set
        score = #{score}
where studentNumber = #{studentNumber} and clazzNumber = #{clazzNumber}

mapper.select:
select * from student_clazz
where studentNumber = #{studentNumber}
        and clazzNumber = #{clazzNumber}

select * from student_clazz


        and studentNumber = #{studentNumber}


        and term = #{term}
