mapper.select:
select * from teacherClass


        and teacherNumber = #{teacherNumber}


        and classNumber = #{classNumber}


        and term = #{term}


        and teacherClassId = #{teacherClassId}

SELECT m.*, n.className FROM ( SELECT term, max(score) maxScore, min(score) minScore, AVG(score) avgScore, classNumber FROM teacherClass a JOIN studentClass b ON a.teacherClassId = b.teacherClassId
and teacherNumber = #{teacherNumber}
GROUP BY term, classNumber ) m JOIN class n ON m.classNumber = n.classNumber
