mapper.update:
update teacher_clazz
set
    term = #{term}
where teacherNumber = #{teacherNumber} and clazzNumber = #{clazzNumber}

mapper.select:
SELECT b.term, max(score) maxScore, min(score) minScore, AVG(score) avgScore, a.clazzNumber FROM teacher_clazz a JOIN student_clazz b ON a.clazzNumber = b.clazzNumber
and teacherNumber = #{teacherNumber}
GROUP BY term, clazzNumber
